import React from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import Product from "../Product";
import "products.scss";

export default function ProductsList(props) {
	const { products } = props;

	const deleteProduct = id => {
		axios.delete("/api/exercises/" + id).then(response => {
			console.log(response.data);
		});

		this.setState({
			exercises: this.state.exercises.filter(el => el.id !== id)
		});
	};

	return (
		<div>
			<h1 className="text-center">EXERCISES</h1>
			<div className="container">
				<div className="row">
					{products.length > 0 ? (
						products.map(p => {
							return (
								<Product product={p} deleteProduct={deleteProduct} key={p.id} />
							);
						})
					) : (
						<p>No exercises created</p>
					)}
				</div>
			</div>
			{
				<Link to="/addexercise" className="btn nav-link lifesports">
					ADD EXERCISE
				</Link>
			}
		</div>
	);
}
