import React from "react";
import { Link } from "react-router-dom";

const Product = props => (
	<div className="exercise-box">
		<div className="card-body">
			<p className="card-header">Date: {props.exercise.username}</p>
			<p className="card-subtitle">Duration: {props.exercise.description}</p>
			<p className="card-text ml-3">{props.exercise.duration}</p>
			<p className="card-text ml-3">{props.exercise.date.substring(0, 10)}</p>
			<Link to={"/editProduct/" + props.exercise._id}>Edit</Link>
			<a
				href="#"
				onClick={() => {
					props.deleteProduct(props.exercise._id);
				}}
			>
				Delete
			</a>
		</div>
	</div>
);

export default function ProductsList(props) {
	const { products } = props;

	const deleteExercise = id => {
		axios.delete("/api/exercises/" + id).then(response => {
			console.log(response.data);
		});

		this.setState({
			exercises: this.state.exercises.filter(el => el._id !== id)
		});
	};

	return (
		<div>
			<h1 className="text-center">EXERCISES</h1>
			<div className="container">
				<div className="row">
					{products.length > 0 ? (
						products.map(p => {
							return (
								<Exercise
									exercise={p}
									deleteExercise={deleteExercise}
									key={p.id}
								/>
							);
						})
					) : (
						<p>No exercises created</p>
					)}
				</div>
			</div>
			{
				<Link to="/addexercise" className="btn nav-link lifesports">
					ADD EXERCISE
				</Link>
			}
		</div>
	);
}
