import React from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import Product from "../Product";
import "./products.scss";

export default function ProductsList(props) {
	const { products } = props;

	const deleteProduct = id => {
		axios.delete("/api/exercises/" + id).then(response => {
			console.log(response.data);
		});

		this.setState({
			exercises: this.state.exercises.filter(el => el.id !== id)
		});
	};

	return (
		<div>
			<table class="table table-striped table-dark">
				<thead>
					<tr>
						<th scope="col-sm-12">Image </th>
						<th scope="col-sm-12">ID</th>
						<th scope="col-sm-12">Name</th>
						<th scope="col-sm-12">Description</th>
					</tr>
				</thead>
				<tbody>
					<tr>
						{products.map(product => {
							return (
								<div>
									<td scope="row">
										<img className="table-img" src={product.img} />
									</td>
									<td>{product.id}</td>
									<td>{product.name}</td>
									<td>{product.description}</td>
								</div>
							);
						})}
					</tr>
				</tbody>
			</table>
		</div>
	);
}
