import React from "react";
import { Link } from "react-router-dom";

export default function Product(props) {
	const { product } = props;
	return (
		<div className="exercise-box">
			<div className="card-body">
				<p className="card-header">Name: {product.name}</p>
				<p className="card-subtitle">Description: {product.description}</p>
				<p className="card-text ml-3">{product.duration}</p>
				<img
					className="card-img"
					src={product.img}
					style={{ height: "auto", width: "500px" }}
				></img>
				<Link to={"/editProduct/" + product._id}>Edit</Link>
				<a
					href="#"
					onClick={() => {
						props.deleteProduct(product._id);
					}}
				>
					Delete
				</a>
			</div>
		</div>
	);
}
